import React, { useEffect, useState } from 'react';
import { ref, get } from 'firebase/database';
import { database } from './Firebase';
import { Bar } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend } from 'chart.js';

ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend);

const TableComponent = () => {
  const [data, setData] = useState([]);
  const [chartData, setChartData] = useState({
    labels: [],
    datasets: [{
      label: 'Plate Number Frequency',
      data: [],
      backgroundColor: [
        'rgba(255, 99, 132, 0.2)',
        'rgba(54, 162, 235, 0.2)',
        'rgba(255, 206, 86, 0.2)'
      ],
      borderColor: [
        'rgba(255, 99, 132, 1)',
        'rgba(54, 162, 235, 1)',
        'rgba(255, 206, 86, 1)'
      ],
      borderWidth: 1
    }]
  });

  useEffect(() => {
    const fetchData = async () => {
      const dbRef = ref(database, '/');
      const snapshot = await get(dbRef);
      const fetchedData = snapshot.val();
      if (fetchedData) {
        const dataArray = Object.values(fetchedData);
        const flatDataArray = dataArray.reduce((acc, cur) => acc.concat(Object.values(cur)), []);
        const reverseData = flatDataArray.reverse();
        setData(reverseData.slice(0, 20));

        // Count frequency of plate names
        const plateCount = {};
        flatDataArray.forEach(item => {
          const plateName = item['Plate Name'];
          if (plateName) {
            plateCount[plateName] = (plateCount[plateName] || 0) + 1;
          }
        });

        // Get the top 3 plate numbers
        const sortedPlates = Object.entries(plateCount).sort((a, b) => b[1] - a[1]).slice(0, 3);
        setChartData({
          labels: sortedPlates.map(x => x[0]),
          datasets: [{
            ...chartData.datasets[0],
            data: sortedPlates.map(x => x[1])
          }]
        });
      }
    };

    fetchData();
  }, []);

  return (
    <div className="container mx-auto my-8 p-4 bg-gray-900 rounded-lg shadow-lg">
      <div className="chart-container" style={{ marginBottom: '20px' }}>
        <Bar data={chartData} options={{ plugins: { legend: { position: 'top' }, title: { display: true, text: 'Top 3 Plate Numbers' } } }} />
      </div>
      <table className="min-w-full table-auto">
        <thead>
          <tr className="bg-gray-700">
            <th className="py-3 px-4 text-left text-gray-200">Date</th>
            <th className="py-3 px-4 text-left text-gray-200">Day</th>
            <th className="py-3 px-4 text-left text-gray-200">Plate Name</th>
            <th className="py-3 px-4 text-left text-gray-200">Status Type</th>
          </tr>
        </thead>
        <tbody>
          {data.map((item, index) => (
            <tr
              key={index}
              className="border-b border-gray-800 odd:bg-gray-800 even:bg-gray-800 hover:bg-gray-600"
            >
              <td className="py-2 px-4 text-gray-300">{item.Date}</td>
              <td className="py-2 px-4 text-gray-300">{item.Day}</td>
              <td className="py-2 px-4 text-gray-300">{item["Plate Name"]}</td>
              <td className="py-2 px-4 text-gray-300">{item.SatusType}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};
