// TableComponent.js
import React, { useEffect, useState } from "react";
import { ref, get } from "firebase/database";
import { database } from "./firebase";

const TableComponent = () => {
  const [data, setData] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      const dbRef = ref(database, "/");
      const snapshot = await get(dbRef);
      const fetchedData = snapshot.val();
      if (fetchedData) {
        const dataArray = Object.values(fetchedData);
        // Flatten the nested objects and convert them to an array
        const flatDataArray = dataArray.reduce(
          (acc, cur) => acc.concat(Object.values(cur)),
          []
        );
        setData(flatDataArray);
      }
    };

    fetchData();
  }, []);

  return (
    <div>
      <h1>Table</h1>
      <table>
        <thead>
          <tr>
            <th>Date</th>
            <th>Day</th>
            <th>Plate Name</th>
            <th>Status Type</th>
          </tr>
        </thead>
        <tbody>
          {data.map((item, index) => (
            <tr key={index}>
              <td>{item.Date}</td>
              <td>{item.Day}</td>
              <td>{item["Plate Name"]}</td>
              <td>{item.StatusType}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default TableComponent;
